// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";

/**
 * @title StBTC
 * @dev Staked Bitcoin token for Babylon protocol
 */
contract StBTC is ERC20, Ownable, Pausable {
    mapping(address => bool) public authorized;

    event AuthorizedAdded(address indexed account);
    event AuthorizedRemoved(address indexed account);

    modifier onlyAuthorized() {
        require(authorized[msg.sender] || msg.sender == owner(), "Not authorized");
        _;
    }

    constructor() ERC20("Staked Bitcoin", "stBTC") {
        // Initial setup
    }

    /**
     * @dev Mint stBTC tokens
     * @param to Address to mint tokens to
     * @param amount Amount of tokens to mint
     */
    function mint(address to, uint256 amount) external onlyAuthorized whenNotPaused {
        _mint(to, amount);
    }

    /**
     * @dev Burn stBTC tokens
     * @param from Address to burn tokens from
     * @param amount Amount of tokens to burn
     */
    function burn(address from, uint256 amount) external onlyAuthorized whenNotPaused {
        _burn(from, amount);
    }

    /**
     * @dev Add authorized minter/burner
     * @param account Address to authorize
     */
    function addAuthorized(address account) external onlyOwner {
        authorized[account] = true;
        emit AuthorizedAdded(account);
    }

    /**
     * @dev Remove authorized minter/burner
     * @param account Address to remove authorization
     */
    function removeAuthorized(address account) external onlyOwner {
        authorized[account] = false;
        emit AuthorizedRemoved(account);
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

contracts/Vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./StBTC.sol";

/**
 * @title Vault
 * @dev Vault contract for managing Bitcoin deposits and stBTC minting
 */
contract Vault is ReentrancyGuard, Ownable, Pausable {
    StBTC public immutable stBTC;

    struct Deposit {
        address user;
        uint256 amount;
        string btcTxHash;
        uint256 timestamp;
        bool processed;
    }

    mapping(string => Deposit) public deposits;
    mapping(address => uint256) public userBalances;

    uint256 public totalDeposits;
    uint256 public constant MINIMUM_DEPOSIT = 0.001 ether; // Minimum deposit amount

    event DepositRegistered(
        string indexed btcTxHash,
        address indexed user,
        uint256 amount,
        uint256 timestamp
    );

    event DepositProcessed(
        string indexed btcTxHash,
        address indexed user,
        uint256 amount
    );

    event WithdrawalRequested(
        address indexed user,
        uint256 amount,
        string btcAddress
    );

    modifier onlyRelayer() {
        require(msg.sender == owner(), "Only relayer can call this function");
        _;
    }

    constructor(address _stBTC) {
        require(_stBTC != address(0), "Invalid stBTC address");
        stBTC = StBTC(_stBTC);
    }

    /**
     * @dev Register a Bitcoin deposit
     * @param btcTxHash Bitcoin transaction hash
     * @param user User address
     * @param amount Amount deposited
     */
    function registerDeposit(
        string calldata btcTxHash,
        address user,
        uint256 amount
    ) external onlyRelayer whenNotPaused {
        require(bytes(btcTxHash).length > 0, "Invalid transaction hash");
        require(user != address(0), "Invalid user address");
        require(amount >= MINIMUM_DEPOSIT, "Amount below minimum");
        require(!deposits[btcTxHash].processed, "Deposit already processed");
        require(deposits[btcTxHash].user == address(0), "Deposit already exists");

        deposits[btcTxHash] = Deposit({
            user: user,
            amount: amount,
            btcTxHash: btcTxHash,
            timestamp: block.timestamp,
            processed: false
        });

        emit DepositRegistered(btcTxHash, user, amount, block.timestamp);
    }

    /**
     * @dev Process a registered deposit and mint stBTC
     * @param btcTxHash Bitcoin transaction hash
     */
    function processDeposit(string calldata btcTxHash) 
        external 
        onlyRelayer 
        whenNotPaused 
        nonReentrant 
    {
        Deposit storage deposit = deposits[btcTxHash];
        require(deposit.user != address(0), "Deposit does not exist");
        require(!deposit.processed, "Deposit already processed");

        deposit.processed = true;
        userBalances[deposit.user] += deposit.amount;
        totalDeposits += deposit.amount;

        // Mint stBTC tokens to user
        stBTC.mint(deposit.user, deposit.amount);

        emit DepositProcessed(btcTxHash, deposit.user, deposit.amount);
    }

    /**
     * @dev Request withdrawal (burns stBTC)
     * @param amount Amount to withdraw
     * @param btcAddress Bitcoin address to withdraw to
     */
    function requestWithdrawal(
        uint256 amount,
        string calldata btcAddress
    ) external whenNotPaused nonReentrant {
        require(amount > 0, "Invalid amount");
        require(bytes(btcAddress).length > 0, "Invalid Bitcoin address");
        require(stBTC.balanceOf(msg.sender) >= amount, "Insufficient balance");

        // Burn stBTC tokens
        stBTC.burn(msg.sender, amount);

        // Update balances
        userBalances[msg.sender] -= amount;
        totalDeposits -= amount;

        emit WithdrawalRequested(msg.sender, amount, btcAddress);
    }

    /**
     * @dev Get deposit information
     * @param btcTxHash Bitcoin transaction hash
     */
    function getDeposit(string calldata btcTxHash) 
        external 
        view 
        returns (Deposit memory) 
    {
        return deposits[btcTxHash];
    }

    /**
     * @dev Pause the contract
     */
    function pause() external onlyOwner {
        _pause();
    }

    /**
     * @dev Unpause the contract
     */
    function unpause() external onlyOwner {
        _unpause();
    }
}

